
public class ejercicio14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=5;
		int i=1;
		for(i=1;i<=n; i++) {
		for (int j=2;j<=i;j++) {
			System.out.print("*");
			}
			System.out.println();
		for (int j=5;j>=i;j--) {
			System.out.print(" ");
				}
		}
	}

}
